function formatTime(t){const e=t%60;return`${Math.floor(t/60).toString().padStart(2,"0")}:${e.toString().padStart(2,"0")}`}document.addEventListener("DOMContentLoaded",(function(){const t=JSON.parse(localStorage.getItem("gameStats")||"{}");t.time&&(document.getElementById("time-stat").textContent=formatTime(t.time)),t.score&&(document.getElementById("score-stat").textContent=t.score),localStorage.removeItem("gameStats")}));